import { defineConfig } from 'vite'
import react from '@vitejs/plugin-react'
import path from 'path'

// https://vitejs.dev/config/
export default defineConfig({
  plugins: [react()],
  resolve: {
    alias: {
      '@': path.resolve(__dirname, './src'),
    },
  },
  base: './', // Ensure relative paths for GH Pages
})

@tailwind base;
@tailwind components;
@tailwind utilities;

html, body, #root {
    height: 100%;
    margin: 0;
    overflow: hidden; /* App handles scrolling */
}

import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Breadcrumbs = () => {
  const location = useLocation();
  const pathSegments = location.pathname.split('/').filter(Boolean);

  // Decodes URI components to handle spaces/special chars in path
  const decodedSegments = pathSegments.map(decodeURIComponent);

  return (
    <nav className="flex px-4 py-3 bg-gray-50 border-b border-gray-200" aria-label="Breadcrumb">
      <ol className="inline-flex items-center space-x-1 md:space-x-3">
        <li className="inline-flex items-center">
          <Link to="/" className="inline-flex items-center text-sm font-medium text-gray-700 hover:text-blue-600">
            <svg className="w-3 h-3 mr-2.5" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 20 20">
              <path d="m19.707 9.293-2-2-7-7a1 1 0 0 0-1.414 0l-7 7-2 2a1 1 0 0 0 1.414 1.414L2 10.414V18a2 2 0 0 0 2 2h3a1 1 0 0 0 1-1v-4a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1v4a1 1 0 0 0 1 1h3a2 2 0 0 0 2-2v-7.586l.293.293a1 1 0 0 0 1.414-1.414Z"/>
            </svg>
            Home
          </Link>
        </li>
        {decodedSegments.map((segment, index) => {
          const to = `/${pathSegments.slice(0, index + 1).join('/')}`;
          return (
            <li key={to}>
              <div className="flex items-center">
                <svg className="w-3 h-3 text-gray-400 mx-1" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 6 10">
                  <path stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="m1 9 4-4-4-4"/>
                </svg>
                <Link to={to} className="ml-1 text-sm font-medium text-gray-700 hover:text-blue-600 md:ml-2">
                  {segment}
                </Link>
              </div>
            </li>
          );
        })}
      </ol>
    </nav>
  );
};

export default Breadcrumbs;

import React from 'react';
import { useNavigate } from 'react-router-dom';

const ExplorerGrid = ({ node }) => {
  const navigate = useNavigate();

  if (!node || !node.children) {
    return <div className="p-4 text-gray-500">Empty directory</div>;
  }

  return (
    <div className="p-4 grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4">
      {node.children.map((child) => (
        <div
          key={child.path}
          onClick={() => navigate(`/${child.path}`)} // HashRouter handles the # automatically if we use Link or navigate
          className="flex flex-col items-center justify-center p-4 bg-white border border-gray-200 rounded-lg shadow-sm hover:bg-gray-50 hover:shadow-md cursor-pointer transition-all aspect-square text-center"
        >
          <div className="mb-2 text-4xl text-gray-400">
            {child.type === 'directory' ? (
              <span role="img" aria-label="folder">üìÅ</span>
            ) : (
              <span role="img" aria-label="file">üìÑ</span>
            )}
          </div>
          <span className="text-sm font-medium text-gray-700 break-words w-full truncate px-2" title={child.name}>
            {child.name}
          </span>
        </div>
      ))}
    </div>
  );
};

export default ExplorerGrid;

import React, { useState, useEffect } from 'react';
import Markdown from 'react-markdown';
import remarkGfm from 'remark-gfm';

const MarkdownViewer = ({ node }) => {
  const [content, setContent] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!node || !node.path) return;

    // Determine the path to fetch. 
    // In scan-dir.js, files are copied to public/notes/${node.path}
    // So we fetch /notes/${node.path}
    const fetchPath = `/notes/${node.path}`;

    setLoading(true);
    fetch(fetchPath)
      .then(res => {
        if (!res.ok) throw new Error('Failed to load document');
        return res.text();
      })
      .then(text => {
        setContent(text);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setError(err.message);
        setLoading(false);
      });
  }, [node]);

  if (loading) return <div className="p-8 text-center text-gray-500">Loading document...</div>;
  if (error) return <div className="p-8 text-center text-red-500">Error: {error}</div>;

  return (
    <div className="max-w-4xl mx-auto p-6 md:p-10 bg-white shadow-sm min-h-screen">
      <div className="prose prose-slate max-w-none">
        <Markdown remarkPlugins={[remarkGfm]}>{content}</Markdown>
      </div>
    </div>
  );
};

export default MarkdownViewer;

import React, { useState, useEffect } from 'react';
import { HashRouter, Routes, Route, useLocation } from 'react-router-dom';
import Breadcrumbs from './components/Breadcrumbs';
import ExplorerGrid from './components/ExplorerGrid';
import MarkdownViewer from './components/MarkdownViewer';

// Helper to find a node in the tree by path
const findNode = (tree, pathSegments) => {
  if (pathSegments.length === 0) return tree;

  // Root node is "Workspace", but our paths start after that usually?
  // Let's check sync-notes.js:
  // tree.name = 'Workspace'
  // children paths are like 'OsCookBook', 'OsCookBook/Linux'

  // If we are at root /, pathSegments is empty. Return tree.

  let currentNode = tree;

  for (const segment of pathSegments) {
    if (!currentNode.children) return null;

    // Decode segment to match node names which might contain spaces
    const decodedSegment = decodeURIComponent(segment);

    const nextNode = currentNode.children.find(child => child.name === decodedSegment);
    if (!nextNode) return null;
    currentNode = nextNode;
  }

  return currentNode;
};

const ContentResolver = ({ tree }) => {
  const location = useLocation();

  // Remove leading slash and split
  const path = location.pathname.substring(1); 
  const segments = path ? path.split('/') : [];

  const node = findNode(tree, segments);

  if (!node) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-xl font-bold text-red-600">404 - Not Found</h2>
        <p className="text-gray-600">The path {location.pathname} does not exist in the index.</p>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-full overflow-hidden">
      <Breadcrumbs />
      <div className="flex-1 overflow-auto bg-gray-100">
        {node.type === 'directory' ? (
          <ExplorerGrid node={node} />
        ) : (
          <MarkdownViewer node={node} />
        )}
      </div>
    </div>
  );
};

function App() {
  const [tree, setTree] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('/directory-tree.json')
      .then(res => {
        if (!res.ok) throw new Error('Failed to load directory index');
        return res.json();
      })
      .then(data => {
        setTree(data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setError(err.message);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-50">
        <div className="text-xl text-gray-600">Loading Workspace Index...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-50">
        <div className="text-xl text-red-600">Error: {error}</div>
      </div>
    );
  }

  return (
    <HashRouter>
      <Routes>
        <Route path="*" element={<ContentResolver tree={tree} />} />
      </Routes>
    </HashRouter>
  );
}

export default App;

