import React, { useState, useEffect } from 'react';
import Markdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import rehypeRaw from 'rehype-raw';
import rehypeSlug from 'rehype-slug';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { oneDark as atomOneDark } from 'react-syntax-highlighter/dist/esm/styles/prism';
import Sidebar from './Sidebar';
import '../markdown-styles.css'; // Import custom styles

const MarkdownViewer = ({ node, siblings }) => {
  const [content, setContent] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [toc, setToc] = useState([]);

  useEffect(() => {
    if (!node || !node.path) return;

    const fetchPath = `/notes/${node.path}`;

    setLoading(true);
    fetch(fetchPath)
      .then(res => {
        if (!res.ok) throw new Error(`Failed to load document (status: ${res.status})`);
        return res.text();
      })
      .then(text => {
        setContent(text);
        setLoading(false);

        // Parse TOC from markdown text
        // Simple regex to find headers like # Header, ## Header
        const lines = text.split('\n');
        const newToc = [];
        // Helper to slugify text to match rehype-slug behavior (simplified)
        // rehype-slug uses github-slugger
        const slugify = (text) => text.toLowerCase().trim().replace(/[^\w\u4e00-\u9fa5]+/g, '-').replace(/^-+|-+$/g, '');

        // We will rely on rehype-slug to generate IDs, but we need to guess them here for the link
        // Or we can just grab the text and hope simple slugify works for now.
        // A better way is to use a remark plugin to extract TOC data, but regex is faster for now.

        lines.forEach(line => {
