const TREE_JSON_PATH = path.join(__dirname, '..', 'public', 'directory-tree.json');
const PROFILE_PHOTO_SRC = path.join(__dirname, '..', 'author_profile', 'profile-photo.jpg');
const PROFILE_PHOTO_DEST = path.join(__dirname, '..', 'public', 'profile-photo.jpg');

// ÂøΩÁï•ÂàóË°®
const IGNORE_PATTERNS = [
    '.git',
    'node_modules',
    'dist',
    'build',
    '.DS_Store',
    'UserTemp' // ÊöÇÊó∂ÂøΩÁï•‰∏¥Êó∂Êñá‰ª∂Â§π
];

async function sync() {
    console.log('üöÄ Starting sync-notes process...');

    // 0. Copy Profile Photo
    if (await fs.pathExists(PROFILE_PHOTO_SRC)) {
        await fs.copy(PROFILE_PHOTO_SRC, PROFILE_PHOTO_DEST);
        console.log('üì∏ Copied profile photo.');
    } else {
        console.warn('‚ö†Ô∏è Profile photo not found.');
    }

    // 1. Ê∏ÖÁêÜÂπ∂ÂàõÂª∫ÁõÆÊ†áÁõÆÂΩï
    await fs.emptyDir(TARGET_NOTES_DIR);
    console.log('‚úÖ Cleaned public/notes directory.');

    const tree = {
        name: 'Workspace',
        type: 'directory',
        children: []
    };

    // 2. ÈÅçÂéÜÊØè‰∏™Ê∫êÁõÆÂΩï
    for (const dirName of SOURCE_DIRS) {
        const sourcePath = path.join(WORKSPACE_ROOT, dirName);

        if (await fs.pathExists(sourcePath)) {
            console.log(`üì¶ Scanning ${dirName}...`);
            const node = await processDirectory(sourcePath, dirName);
            if (node && node.children.length > 0) {
                tree.children.push(node);
            }
        } else {
            console.warn(`‚ö†Ô∏è  Warning: Directory ${dirName} not found at ${sourcePath}`);
        }
    }

    // 3. ÂÜôÂÖ•Á¥¢ÂºïÊñá‰ª∂
    await fs.writeJson(TREE_JSON_PATH, tree, { spaces: 2 });
    console.log(`‚ú® Generated index: ${TREE_JSON_PATH}`);
    console.log('üéâ Sync complete!');
}

async function processDirectory(currentPath, relativePath) {
    const stats = await fs.stat(currentPath);
    const name = path.basename(currentPath);
    const mtime = stats.mtime.toISOString(); // Capture modification time

    if (stats.isDirectory()) {
        // ÂøΩÁï•ÊåáÂÆöÁõÆÂΩï
        if (IGNORE_PATTERNS.includes(name)) return null;

        const children = await fs.readdir(currentPath);
        const node = {
            name,
            type: 'directory',
            path: relativePath,
            mtime, // Add mtime
            children: []
        };

        for (const child of children) {
            const childPath = path.join(currentPath, child);
            const childRelativePath = path.join(relativePath, child);
            const childNode = await processDirectory(childPath, childRelativePath);
            if (childNode) {
                node.children.push(childNode);
            }
        }

        // Â¶ÇÊûúÊñá‰ª∂Â§πÂÜÖÊ≤°ÊúâÂÜÖÂÆπÔºàÂèØËÉΩÊòØË¢´ËøáÊª§Êéâ‰∫ÜÔºâÔºåÂàô‰∏çÊòæÁ§∫ËØ•Êñá‰ª∂Â§π
        if (node.children.length > 0) {
            // Normalize path separators to forward slashes for web usage
            node.path = node.path.split(path.sep).join('/');
            return node;
        }
        return null;
    } else {
        // Âè™‰øùÁïô Markdown Êñá‰ª∂
        if (path.extname(name).toLowerCase() === '.md') {
            // Â∞ÜÊñá‰ª∂Â§çÂà∂Âà∞ public/notes ‰∏ãÂØπÂ∫îÁöÑÂ±ÇÁ∫ß
            const destPath = path.join(TARGET_NOTES_DIR, relativePath);
            await fs.ensureDir(path.dirname(destPath));
            await fs.copy(currentPath, destPath);

            return {
                name,
                type: 'file',
                path: relativePath.split(path.sep).join('/'), // Normalize to forward slashes
                mtime // Add mtime
            };
        }
        return null;
    }
}

